import React from "react";

import GoogleMapReact from "google-map-react";
import { Paper, Typography, useMediaQuery } from "@material-ui/core";
import LocationOnOutlined from "@material-ui/icons/LocationOnOutlined";
import Rating from "@material-ui/lab";

import useStyles from "./styles.js";

function Map({ setCoordinates, setBounds, coordinates, places }){
    const classes = useStyles();
    const isDesktop = useMediaQuery('(min-width: 600px)');

    return (
        <div className={classes.mapContainer}>
            <GoogleMapReact
                bootstrapURLKeys = {{key: 'AIzaSyASOkRIRbXtXgPUxtpKGKORkNZ2-mTvrZQ'}}
                defaultCenter = {coordinates}
                center = {coordinates}
                defaultZoom = {14}
                margin = {[50, 50, 50, 50]}
                options = {''}
                onChange = {(event) =>{
                    setCoordinates({ lat: event.center.lat, lng: event.center.lng });
                    setBounds({ ne: event.marginBounds.ne, sw: event.marginBounds.sw });
                }}
                onChildClick = {''}
            >
                {places?.map((place, i) =>{
                    <div
                        className = {classes.markerContainer}
                        lat = {Number(place.latitude)}
                        lng = {Number(place.longitude)}
                        key = {i}
                        >
                            {!isDesktop ? (
                                <LocationOnOutlined color="primary" fontSize="large" />
                            ) : (
                                <Paper elevation={3} className={classes.paper}>
                                    <Typography gutterBottom variant="subtitle2" className={classes.typography}>
                                        {place.name}
                                    </Typography>
                                    <img
                                        className={classes.pointer}
                                        src= {place.photo ? place.photo.images.large.url : 'https://images.pexels.com/photos/262978/pexels-photo-262978.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2'}
                                        alt = {place.name}
                                    />
                                </Paper>
                            )}
                    </div>
                })}
            </GoogleMapReact>
        </div>
    )
}

export default Map;
